package com.myapp.demo.client;

public class Person {
	

	private Integer id;
	private String role;
	
	public Person() {
		// TODO Auto-generated constructor stub
	}

	public Person(String role) {
		super();
		this.role = role;
	}

	public Integer getId() {
		return id;
	}

	public void setId(Integer id) {
		this.id = id;
	}

	public String getRole() {
		return role;
	}

	public void setRole(String role) {
		this.role = role;
	}
	
	
	

}
